mapper.select:
SELECT BLOG_ID
FROM TB_BLOG
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(1)
FROM TB_FOLLOW
WHERE FOLLOWED_BLOG_ID = #{blogId}

SELECT COUNT(1)
FROM TB_FOLLOW
WHERE FOLLOWER_BLOG_ID = #{blogId}

mapper.insert:
INSERT INTO TB_FOLLOW (FOLLOWER_BLOG_ID, FOLLOWED_BLOG_ID)
SELECT BLOG_ID, #{followeeId}
FROM TB_BLOG
WHERE MEMBER_ID = #{followerId}

mapper.delete:
DELETE FROM TB_FOLLOW
WHERE FOLLOWER_BLOG_ID IN (
    SELECT BLOG_ID
    FROM TB_BLOG
    WHERE MEMBER_ID = #{followerId}
)
AND FOLLOWED_BLOG_ID = #{followeeId}
