mapper.select:
SELECT PASSWORD
FROM TB_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE TB_MEMBER


        NICKNAME = #{nickname},


        PASSWORD = #{newPassword},


        PHONE = #{phone}


WHERE MEMBER_ID = #{memberId}

UPDATE TB_MEMBER_DETAIL


        HEIGHT = #{height},


        WEIGHT = #{weight}


WHERE MEMBER_ID = #{memberId}
